$(window).load(function(){$(".flexslider").flexslider({animation:"slide"})});$(document).ready(function(){$("div.main").fitVids();$("img.lazy").lazyload();$(".expandable").expander({expandText:"more",userCollapseText:"less",expandSpeed:400,collapseSpeed:400,moreClass:"read-more",lessClass:"read-less",slicePoint:800});$(".album-block img").click(function(){$(this).parent().parent().toggleClass("flipped")});$.localScroll({hash:!0,duration:400});$(window).load(function(){var e=$(window).width();if(e<960){$.stratus({links:"https://soundcloud.com/middlewestmgmt/sets/middlewestmgmt-com",stats:!1});$(".share").css("display","none")}else $.stratus({links:"https://soundcloud.com/middlewestmgmt/sets/middlewestmgmt-com"})});$(".colorbox").colorbox({rel:"group"});$(".colorbox-iframe").colorbox({iframe:!0,width:"80%",height:"80%"})});