$(document).ready(function(){$(".flexslider").flexslider({animation:"slide"});$("div.main").fitVids();$("img.lazy").lazyload({effect:"fadeIn",threshold:200});$(".expandable").expander({expandText:"more",userCollapseText:"less",expandSpeed:400,collapseSpeed:400,moreClass:"read-more",lessClass:"read-less",slicePoint:800});$(".album-block img").click(function(){$(this).parent().parent().toggleClass("flipped")});$.localScroll({hash:!0,duration:400});$(window).load(function(){var e=$(window).width();if(e<960){$.stratus({links:"https://soundcloud.com/middlewestmgmt/sets/middlewestmgmt-com",stats:!1});$(".share").css("display","none")}else $.stratus({links:"https://soundcloud.com/middlewestmgmt/sets/middlewestmgmt-com"})});var e=$(window).width();console.log(e);if(e>=768){$(".colorbox").colorbox({rel:"group"});$(".colorbox-iframe").colorbox({iframe:!0,width:"80%",height:"80%"})}else{$(".colorbox").click(function(){var e=$(this).parent().next();$(e).toggleClass("show");return!1});$(".mobile-album-info-close").click(function(){$(".mobile-album-info").removeClass("show")})}});